import React, { useState, useEffect, useContext, useRef } from "react";
import "./Events.css";
import Modal from "./Modal";
import Backdrop from "./Backdrop/Backdrop";
import Spinner from "./Spinner/Spinner";
import EventList from "../Event/EventList";
import { useAuth } from "../../context/auth-context";

const EventsPage = () => {
  const [creating, setCreating] = useState(false);
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const authContext = useAuth(); 


  const titleElRef = useRef();
  const priceElRef = useRef();
  const dateElRef = useRef();
  const descriptionElRef = useRef();

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    setIsLoading(true);
    const requestBody = {
      query: `query {
        events {
          _id
          title
          description
          date
          price
          creator {
            _id
            email
          }
        }
      }`
    };

    try {
      const res = await fetch("http://localhost:8000/graphql", {
        method: "POST",
        body: JSON.stringify(requestBody),
        headers: {
          "Content-Type": "application/json"
        }
      });

      if (res.status !== 200 && res.status !== 201) {
        throw new Error("Failed");
      }

      const resData = await res.json();
      const fetchedEvents = resData.data.events;
      setEvents(fetchedEvents);
    } catch (err) {
      console.log(err);
    } finally {
      setIsLoading(false);
    }
  };

  const startCreateEventHandler = () => {
    setCreating(true);
  };

  const modalConfirmHandler = async () => {
    setCreating(false);
    const title = titleElRef.current.value;
    const price = +priceElRef.current.value;
    const date = dateElRef.current.value;
    const description = descriptionElRef.current.value;

    if (
      title.trim().length === 0 ||
      price <= 0 ||
      date.trim().length === 0 ||
      description.trim().length === 0
    ) {
      return;
    }

    const event = { title, price, date, description };
    const requestBody = {
      query: `mutation {
        createEvent(eventInput: {title: "${title}", description: "${description}", date: "${date}", price: ${price}}) {
          _id
          title
          description
          date
          price
        }
      }`
    };

    try {
      const res = await fetch("http://localhost:8000/graphql", {
        method: "POST",
        body: JSON.stringify(requestBody),
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token
        }
      });

      if (res.status !== 200 && res.status !== 201) {
        throw new Error("Failed");
      }

      const resData = await res.json();
      setEvents((prevEvents) => [
        ...prevEvents,
        {
          _id: resData.data.createEvent._id,
          title: resData.data.createEvent.title,
          description: resData.data.createEvent.description,
          date: resData.data.createEvent.date,
          price: resData.data.createEvent.price,
          creator: {
            _id: context.userId
          }
        }
      ]);
    } catch (err) {
      console.log(err);
    }
  };

  const modalCancelHandler = () => {
    setCreating(false);
    setSelectedEvent(null);
  };

  const showDetailHandler = (eventId) => {
    const selected = events.find((e) => e._id === eventId);
    setSelectedEvent(selected);
  };

  const bookEventHandler = async () => {
    if (!authContext.token) {
      setSelectedEvent(null);
      return;
    }

    const requestBody = {
      query: `mutation {
        bookEvent(eventId: "${selectedEvent._id}") {
          _id
          createdAt
          updatedAt
        }
      }`
    };

    try {
      const res = await fetch("http://localhost:8000/graphql", {
        method: "POST",
        body: JSON.stringify(requestBody),
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + authContext.token
        }
      });

      if (res.status !== 200 && res.status !== 201) {
        throw new Error("Failed");
      }

      const resData = await res.json();
      console.log(resData);
      setSelectedEvent(null);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <React.Fragment>
      {creating && (
        <React.Fragment>
          <Backdrop />
          <Modal
            title="Add Event"
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={modalConfirmHandler}
            confirmText="Confirm"
          >
            <form>
              <div className="form-control">
                <label htmlFor="title">Title</label>
                <input type="text" id="title" ref={titleElRef} />
              </div>

              <div className="form-control">
                <label htmlFor="price">Price</label>
                <input type="number" id="price" ref={priceElRef} />
              </div>

              <div className="form-control">
                <label htmlFor="date">Date</label>
                <input type="datetime-local" id="date" ref={dateElRef} />
              </div>

              <div className="form-control">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  rows="4"
                  ref={descriptionElRef}
                />
              </div>
            </form>
          </Modal>
        </React.Fragment>
      )}
      {selectedEvent && (
        <React.Fragment>
          <Backdrop />
          <Modal
            title={selectedEvent.title}
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={bookEventHandler}
            confirmText={context.token ? "Book it!" : "Confirm"}>
            <h2 className="event-item-price">Price: ${selectedEvent.price.toFixed(2)}</h2>
            <p className="event-item-date">
              Date: {new Date(selectedEvent.date).toLocaleDateString()}
            </p>
            <p className="event-item-description">Description: {selectedEvent.description}</p>
          </Modal>
        </React.Fragment>
      )}
      {context.token && (
        <div className="events-control">
          <p>Share your own events!</p>
          <button className="btn" onClick={startCreateEventHandler}>
            Create Event
          </button>
        </div>
      )}
      {isLoading ? (
        <Spinner />
      ) : (
        <EventList
          events={events}
          authUserId={context.userId}
          onViewDetails={showDetailHandler}
        />
      )}
    </React.Fragment>
  );
};

export default EventsPage;
