// src/components/Events/Modal.js
import React from "react";
import "./Modal.css";

const Modal = ({ title, canCancel, canConfirm, onCancel, onConfirm, confirmText, disableConfirmButton, children }) => {
  return (
    <div className="modal">
      <header className="modal__header">
        <h1>{title}</h1>
      </header>
      <section className="modal__content">{children}</section>
      <footer className="modal__footer">
        {canCancel && <button className="btn" onClick={onCancel}>Cancel</button>}
        {canConfirm && (
          <button className="btn" onClick={onConfirm} disabled={disableConfirmButton}>
            {confirmText}
          </button>
        )}
      </footer>
    </div>
  );
};

export default Modal;
