import React from "react";
import "./EventItem.css";

const EventItem = ({ event, authUserId, onDetail }) => {
  if (!event) {
    return <p>No Events</p>;
  }

  return (
    <li key={event._id} className="events-list-item">
      <div className="event-item-content">
        <h1 className="event-item-title">{event.title}</h1>
        <h2 className="event-item-price">${event.price.toFixed(2)}</h2>
        <p className="event-item-date">
          Date: {new Date(event.date).toLocaleDateString()}
        </p>
      </div>
      <div className="event-item-action">
        {authUserId !== event.creator._id ? (
          <button
            className="btn btn-view-details"
            onClick={() => onDetail(event._id)}
          >
            View Details
          </button>
        ) : (
          <p className="event-owner">You are the owner of this event</p>
        )}
      </div>
    </li>
  );
};

export default EventItem;
