import React from "react";
import "./Modal.css";
import { useAuth } from "../../context/auth-context";

const Modal = (props) => {
const authContext = useAuth(); 
  const isAuthorized = authContext.isAuth;
  return (
    <div className="modal-overlay" onClick={props.onCancel}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <header className="modal-header">
          <h1>{props.title}</h1>
        </header>
        <section className="modal-content">
          {props.children}
        </section>
        <section className="modal-actions">
          {props.canCancel && (
            <button className="btn btn-cancel" onClick={props.onCancel}>
              Cancel
            </button>
          )}
          {props.canConfirm && isAuthorized && ( // Check if the user is authorized
            <button className="btn btn-confirm" onClick={props.onConfirm}>
              {props.confirmText}
            </button>
          )}
        </section>
      </div>
    </div>
  );
};

export default Modal;
