import React, { useState, useEffect, useRef, useCallback } from "react";
import "./Events.css";
import Modal from "../components/Modal/Modal";
import Backdrop from "../components/Backdrop/Backdrop";
import { useAuth } from "../context/auth-context";
import Spinner from "../components/Spinner/Spinner";
import EventList from "../components/Events/EventList/EventList";

const EventsPage = () => {
  const [creating, setCreating] = useState(false);
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isBooked, setIsBooked] = useState(false);

  const auth = useAuth();

  const titleElRef = useRef();
  const priceElRef = useRef();
  const dateElRef = useRef();
  const descriptionElRef = useRef();

  // Fetch events once the component is mounted
  useEffect(() => {
    fetchEvents();
  }, []);

  // Handle the creation modal visibility
  const startCreateEventHandler = () => setCreating(true);
  const modalCancelHandler = () => {
    setCreating(false);
    setSelectedEvent(null);
  };

  // Create an event by sending a mutation request
  const createEvent = (title, price, date, description) => {
    const requestBody = {
      query: `
        mutation {
          createEvent(eventInput: {
            title: "${title}", 
            description: "${description}", 
            date: "${date}", 
            price: ${price}
          }) {
            _id
            title
            description
            date
            price
          }
        }`,
    };
    const token = auth.token;

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then((resData) => {
        const newEvent = {
          ...resData.data.createEvent,
          creator: { _id: auth.userId },
        };
        setEvents((prevEvents) => [...prevEvents, newEvent]);
        setCreating(false);
      })
      .catch((err) => console.log(err));
  };

  const modalConfirmHandler = () => {
    const title = titleElRef.current.value;
    const price = +priceElRef.current.value;
    const date = dateElRef.current.value;
    const description = descriptionElRef.current.value;

    if (title.trim().length === 0 || price <= 0 || date.trim().length === 0 || description.trim().length === 0) {
      return;
    }
    createEvent(title, price, date, description);
  };

  // Fetch events from the server
  const fetchEvents = useCallback(() => {
    setIsLoading(true);
    const requestBody = {
      query: `
        query {
          events {
            _id
            title
            description
            date
            price
            creator {
              _id
              email
            }
          }
        }
      `,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then((resData) => {
        setEvents(resData.data.events);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setIsLoading(false);
      });
  }, []);

  const showDetailHandler = (eventId) => {
    const selectedEvent = events.find((e) => e._id === eventId);
    setSelectedEvent(selectedEvent);
    setIsBooked(selectedEvent.isBooked);
  };

  const bookEventHandler = () => {
    if (!auth.token) {
      setSelectedEvent(null);
      return;
    }

    if (isBooked) {
      alert("Booking already done for this event.");
      return;
    }

    const requestBody = {
      query: `
        mutation {
          bookEvent(eventId: "${selectedEvent._id}") {
            _id
            createdAt
            updatedAt
          }
        }`,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + auth.token,
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then(() => {
        setSelectedEvent(null);
        setIsBooked(true);
      })
      .catch((err) => console.log(err));
  };

  // JSX for rendering UI components
  return (
    <>
      {creating && (
        <>
          <Backdrop />
          <Modal
            title="Add Event"
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={modalConfirmHandler}
            confirmText="Confirm"
          >
            <form>
              <div className="form-control">
                <label htmlFor="title">Title</label>
                <input type="text" id="title" ref={titleElRef} />
              </div>
              <div className="form-control">
                <label htmlFor="price">Price</label>
                <input type="number" id="price" ref={priceElRef} />
              </div>
              <div className="form-control">
                <label htmlFor="date">Date</label>
                <input type="datetime-local" id="date" ref={dateElRef} />
              </div>
              <div className="form-control">
                <label htmlFor="description">Description</label>
                <textarea id="description" rows="4" ref={descriptionElRef} />
              </div>
            </form>
          </Modal>
        </>
      )}
      {selectedEvent && (
        <>
          <Backdrop />
          <Modal
            title={selectedEvent.title}
            canCancel
            canConfirm={auth.token}
            onCancel={modalCancelHandler}
            onConfirm={bookEventHandler}
            confirmText={isBooked ? "Already Booked" : "Book Event"}
          >
            <h1>{selectedEvent.title}</h1>
            <h2>
              {new Date(selectedEvent.date).toLocaleDateString()} - $
              {selectedEvent.price}
            </h2>
            <p>{selectedEvent.description}</p>
          </Modal>
        </>
      )}
      {auth.token && (
        <div className="events-control">
          <p>Create a new event:</p>
          <button className="btn" onClick={startCreateEventHandler}>
            Create Event
          </button>
        </div>
      )}
      {isLoading ? (
        <Spinner />
      ) : (
        <EventList events={events} onViewDetail={showDetailHandler} />
      )}
    </>
  );
};

export default EventsPage;
