import { useState, useCallback } from "react";
import { useSelector } from "react-redux";
import { graphqlRequest } from "../graphqlClient";

export const useEvents = () => {
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const token = useSelector((state) => state.auth.token);

  const fetchEvents = useCallback(async () => {
    setIsLoading(true);
    const requestBody = {
      query: `
        query {
          events {
            _id
            title
            description
            date
            price
            creator { _id }
          }
        }
      `
    };

    try {
      const resData = await graphqlRequest(requestBody.query);
      setEvents(events);
      console.log()
    } catch (error) {
      console.error(error); // Log error for debugging
      // You might want to set an error state here to show an error message to the user
    } finally {
      setIsLoading(false);
    }
  }, []);

  const createEvent = async (eventData) => {
    const requestBody = {
      query: `
        mutation CreateEvent($title: String!, $description: String!, $date: String!, $price: Float!) {
          createEvent(eventInput: { title: $title, description: $description, date: $date, price: $price }) {
            _id
            title
            description
            date
            price
          }
        }
      `,
      variables: {
        title: eventData.title,
        description: eventData.description,
        date: eventData.date,
        price: eventData.price
      }
    };

    try {
      const resData = await graphqlRequest(requestBody.query, requestBody.variables, token);
      setEvents((prevEvents) => [...prevEvents, resData.data.createEvent]);
    } catch (error) {
      console.error(error);
    }
  };

  const bookEvent = async (eventId) => {
    const requestBody = {
      query: `
        mutation BookEvent($eventId: ID!) {
          bookEvent(eventId: $eventId) {
            _id
          }
        }
      `,
      variables: {
        eventId: eventId
      }
    };

    try {
      await graphqlRequest(requestBody.query, requestBody.variables, token);
    } catch (error) {
      console.error(error); // Handle error appropriately
    }
  };

  return {
    events,
    isLoading,
    fetchEvents,
    createEvent,
    bookEvent,
  };
};

export default useEvents;
