import React, { useState, useEffect, useRef, useCallback } from "react";
import "./Events.css";
import Modal from "./Modal.js";
import Backdrop from "./Backdrop/Backdrop.js";
import { useAuth } from "../../context/auth-context.js";
import Spinner from "./Spinner/Spinner.js";
import EventList from "./EventList.js";

const EventsPage = () => {
  const [creating, setCreating] = useState(false);
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isBooked, setIsBooked] = useState(false); // Track if the event is booked

  const auth = useAuth();

  const titleElRef = useRef();
  const priceElRef = useRef();
  const dateElRef = useRef();
  const descriptionElRef = useRef();

  useEffect(() => {
    fetchEvents();
  }, []);

  const startCreateEventHandler = () => {
    setCreating(true);
  };

  const modalConfirmHandler = () => {
    setCreating(false);
    const title = titleElRef.current.value;
    const price = +priceElRef.current.value;
    const date = dateElRef.current.value;
    const description = descriptionElRef.current.value;

    // Validation check
    if (
      title.trim().length === 0 ||
      price <= 0 ||
      date.trim().length === 0 ||
      description.trim().length === 0
    ) {
      return;
    }

    const event = { title, price, date, description };

    const requestBody = {
      query: `mutation {
        createEvent(eventInput: {title: "${title}", description: "${description}", date: "${date}", price: ${price}}) {
          _id
          title
          description
          date
          price
        }
      }`,
    };

    const token = auth.token;

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then((resData) => {
        setEvents((prevEvents) => [
          ...prevEvents,
          {
            _id: resData.data.createEvent._id,
            title: resData.data.createEvent.title,
            description: resData.data.createEvent.description,
            date: resData.data.createEvent.date,
            price: resData.data.createEvent.price,
            creator: {
              _id: auth.userId,
            },
          },
        ]);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const modalCancelHandler = () => {
    setCreating(false);
    setSelectedEvent(null);
  };

  const fetchEvents = useCallback(() => {
    setIsLoading(true);
    const requestBody = {
      query: `query {
        events {
          _id
          title
          description
          date
          price
          creator {
            _id
            email
          }
        }
      }`,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then((resData) => {
        setEvents(resData.data.events);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setIsLoading(false);
      });
  }, []);

  const showDetailHandler = (eventId) => {
    const selectedEvent = events.find((e) => e._id === eventId);
    setSelectedEvent(selectedEvent);

    // Check if the event has already been booked by the user
    fetch(`http://localhost:8000/graphql`, {
      method: "POST",
      body: JSON.stringify({
        query: `
          query {
            userBookings {
              event {
                _id
              }
            }
          }
        `,
      }),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + auth.token,
      },
    })
      .then((res) => res.json())
      .then((resData) => {
        const userBookings = resData.data.userBookings;
        const isEventBooked = userBookings.some(
          (booking) => booking.event._id === selectedEvent._id
        );
        setIsBooked(isEventBooked);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const bookEventHandler = () => {
    if (!auth.token) {
      setSelectedEvent(null);
      return;
    }

    if (isBooked) {
      alert("Booking already done for this event.");
      return;
    }

    const requestBody = {
      query: `mutation {
        bookEvent(eventId: "${selectedEvent._id}") {
          _id
          createdAt
          updatedAt
        }
      }`,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + auth.token,
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then((resData) => {
        console.log(resData);
        setSelectedEvent(null);
        setIsBooked(true); // Set the booking status to true
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      {creating && (
        <>
          <Backdrop />
          <Modal
            title="Add Event"
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={modalConfirmHandler}
            confirmText="Confirm"
          >
            <div className="modal-content">
              <form>
                <div className="form-control">
                  <label htmlFor="title">Title</label>
                  <input type="text" id="title" ref={titleElRef} />
                </div>
                <div className="form-control">
                  <label htmlFor="price">Price</label>
                  <input type="number" id="price" ref={priceElRef} />
                </div>
                <div className="form-control">
                  <label htmlFor="date">Date</label>
                  <input type="datetime-local" id="date" ref={dateElRef} />
                </div>
                <div className="form-control">
                  <label htmlFor="description">Description</label>
                  <textarea id="description" rows="4" ref={descriptionElRef} />
                </div>
              </form>
            </div>
          </Modal>
        </>
      )}
      {selectedEvent && (
        <>
          <Backdrop />
          <Modal
            title={selectedEvent.title}
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={bookEventHandler}
            confirmText={isBooked ? "Booked" : "Book"}
          >
            <div className="modal-content">
              <h2>{selectedEvent.title}</h2>
              <p>{selectedEvent.description}</p>
              <p>
                <strong>Price: ${selectedEvent.price}</strong>
              </p>
              <p>
                <strong>Date: {new Date(selectedEvent.date).toLocaleString()}</strong>
              </p>
            </div>
          </Modal>
        </>
      )}
      <div className="events-control">
        <p>Share your own Events!</p>
        <button className="btn" onClick={startCreateEventHandler}>
          Create Event
        </button>
      </div>
      {isLoading ? (
        <Spinner />
      ) : (
        <EventList events={events} onDetail={showDetailHandler} />
      )}
    </>
  );
};

export default EventsPage;
