import React, { useState, useEffect, useRef } from "react";
import "./Events.css";
import Modal from "./Modal/Modal";
import Backdrop from "./Backdrop/Backdrop";
import Spinner from "./Spinner/Spinner";
import EventList from "./EventList/EventList";
import useEvents from "../../controller/eventController/eventController.js";
import { useSelector } from "react-redux";

const EventsPage = () => {
  const [creating, setCreating] = useState(false);
  const [updating, setUpdating] = useState(false); // State for updating
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const { events, isLoading, fetchEvents, createEvent, updateEvent, bookEvent } = useEvents();
  const token = useSelector((state) => state.auth.token);
  const userId = useSelector((state) => state.auth.userId);

  const titleElRef = useRef();
  const priceElRef = useRef();
  const dateElRef = useRef();
  const descriptionElRef = useRef();

  useEffect(() => {
    fetchEvents();
  }, [fetchEvents]);

  const startCreateEventHandler = () => {
    setCreating(true);
    setUpdating(false); // Reset updating state
  };

  const startUpdateEventHandler = (event) => {
    setCreating(false);
    setUpdating(true);
    setSelectedEvent(event);
    titleElRef.current.value = event.title;
    priceElRef.current.value = event.price;
    dateElRef.current.value = event.date.split('T')[0]; // Set the date input value
    descriptionElRef.current.value = event.description;
  };

  const modalConfirmHandler = async () => {
    setErrorMessage(""); 
    const title = titleElRef.current.value;
    const price = +priceElRef.current.value;
    const date = dateElRef.current.value;
    const description = descriptionElRef.current.value;

    if (
      title.trim().length === 0 ||
      price <= 0 ||
      date.trim().length === 0 ||
      description.trim().length === 0
    ) {
      setErrorMessage("Please fill in all fields correctly.");
      return;
    }

    if (updating && selectedEvent) {
      await updateEvent(selectedEvent._id, { title, description, date, price });
    } else {
      await createEvent({ title, description, date, price });
    }

    setCreating(false); 
    setUpdating(false);
    setSelectedEvent(null);
  };

  const modalCancelHandler = () => {
    setCreating(false);
    setUpdating(false);
    setSelectedEvent(null);
    setErrorMessage(null);
  };

  const showDetailHandler = (eventId) => {
    const selected = events.find((e) => e._id === eventId);
    setSelectedEvent(selected);
    startUpdateEventHandler(selected); // Start updating
  };

  const bookEventHandler = async () => {
    if (!token) {
      setSelectedEvent(null);
      return;
    }

    await bookEvent(selectedEvent._id);
    setSelectedEvent(null);
  };

  return (
    <>
      {(creating || updating) && (
        <>
          <Backdrop />
          <Modal
            title={updating ? "Update Event" : "Add Event"}
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={modalConfirmHandler}
            confirmText="Confirm"
          >
            <form>
              <div className="form-control">
                <label htmlFor="title">Title</label>
                <input type="text" id="title" ref={titleElRef} required />
              </div>

              <div className="form-control">
                <label htmlFor="price">Price</label>
                <input type="number" id="price" ref={priceElRef} required />
              </div>

              <div className="form-control">
                <label htmlFor="date">Date</label>
                <input type="date" id="date" ref={dateElRef} required />
              </div>

              <div className="form-control">
                <label htmlFor="description">Description</label>
                <textarea id="description" rows="4" ref={descriptionElRef} required />
              </div>
              {errorMessage && <p className="error-message">{errorMessage}</p>}
            </form>
          </Modal>
        </>
      )}
      {selectedEvent && !updating && (
        <>
          <Backdrop />
          <Modal
            title={selectedEvent.title}
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={bookEventHandler}
            confirmText="Book"
          >
            <h2>{selectedEvent.description}</h2>
            <p>
              <strong>Price: ${selectedEvent.price}</strong>
            </p>
          </Modal>
        </>
      )}
      {isLoading ? (
        <Spinner />
      ) : (
        <>
          <div className="events-control">
            <p>Share your own events!</p>
            <button className="btn" onClick={startCreateEventHandler}>
              Create Event
            </button>
          </div>
          <EventList events={events} onDetail={showDetailHandler} />
        </>
      )}
    </>
  );
};

export default EventsPage;
