import React, { useState } from "react";
import "./App.css";
import { BrowserRouter, Route, Navigate, Routes } from "react-router-dom";
import AuthPage from "./components/Auth/Auth";
import BookingsPage from "./components/Bookings/Bookings";
import EventsPage from "./components/Event/Events";
import MainNavigation from "./components/Navigation/MainNavigation";
import AuthContext from "./context/auth-context";

const App = () => {
  const [authState, setAuthState] = useState({
    token: null,
    userId: null,
  });

  const login = (token, userId) => {
    setAuthState({ token, userId });
  };

  const logout = () => {
    setAuthState({ token: null, userId: null });
  };

  return (
    <BrowserRouter>
      <AuthContext.Provider
        value={{
          token: authState.token,
          userId: authState.userId,
          login,
          logout,
        }}
      >
        <MainNavigation />
        <Routes>
          {authState.token ? (
            <>
              <Route path="/" element={<Navigate to="/events" />} />
              <Route path="/auth" element={<Navigate to="/events" />} />
              <Route path="/events" element={<EventsPage />} />
              <Route path="/bookings" element={<BookingsPage />} />
            </>
          ) : (
            <>
              <Route path="/auth" element={<AuthPage />} />
              <Route path="/events" element={<EventsPage />} />
              <Route path="*" element={<Navigate to="/auth" />} />
            </>
          )}
        </Routes>
      </AuthContext.Provider>
    </BrowserRouter>
  );
};

export default App;
