import { useState, useCallback } from "react";
import { useSelector } from "react-redux";
import { graphqlRequest } from "../graphqlClient";

export const useEvents = () => {
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const token = useSelector((state) => state.auth.token);
  const userId = useSelector((state) => state.auth.userId);
  const fetchEvents = useCallback(async () => {
    setIsLoading(true);
    setError(null);  // Reset error on fetch start

    const requestBody = {
      query: `
        query {
          events {
            _id
            title
            description
            date
            price
          }
        }
      `
    };

    try {
      const resData = await graphqlRequest(requestBody.query, null, token);
      if (resData && resData.data && resData.data.events) {

        setEvents(resData.data.events);
      }
    } catch (error) {
      console.error(error); // Log error for debugging
      setError("Failed to load events. Please try again.");
    } finally {
      setIsLoading(false);
    }
  }, [token]);  // Make sure to include token if needed

  const createEvent = async (eventData) => {
    const requestBody = {
      query: `
        mutation CreateEvent($title: String!, $description: String!, $date: String!, $price: Float!) {
          createEvent(eventInput: { title: $title, description: $description, date: $date, price: $price }) {
            _id
            title
            description
            date
            price
            creator {
              _id
            }
          }
        }
      `,
      variables: {
        title: eventData.title,
        description: eventData.description,
        date: eventData.date,
        price: eventData.price,
      },
    };

    try {
      const resData = await graphqlRequest(requestBody.query, requestBody.variables, token);
      console.log('Created Event:', resData.data.createEvent);

      // Option 1: Append the new event to the current list
      setEvents((prevEvents) => [
        ...prevEvents,
        {
          _id: resData.data.createEvent._id,
          title: resData.data.createEvent.title,
          description: resData.data.createEvent.description,
          date: resData.data.createEvent.date,
          price: resData.data.createEvent.price,
          creator: {
            _id: userId, // Set the current user's ID as the creator
          },
        },
      ]);


      // Option 2: Refetch the events list after creating a new event
      // await fetchEvents();

    } catch (error) {
      console.error('Failed to create event:', error);
      setError("Failed to create event. Please try again.");
    }
  };

  const bookEvent = async (eventId) => {
    const requestBody = {
      query: `
        mutation BookEvent($eventId: ID!) {
          bookEvent(eventId: $eventId) {
            _id
          }
        }
      `,
      variables: { eventId }
    };

    try {
      await graphqlRequest(requestBody.query, requestBody.variables, token);
    } catch (error) {
      console.error(error);
      setError("Failed to book the event. Please try again.");
    }
  };

  return {
    events,
    isLoading,
    error,
    fetchEvents,
    createEvent,
    bookEvent,
  };
};

export default useEvents;
