import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "./MainNavigation.css"; // Ensure you have the correct path to Navbar.css
import { useAuth } from "../../context/auth-context";

const MainNavigation = () => {
  const { token, logout } = useAuth();
  const [menuIsOpen, setMenuIsOpen] = useState(false);

  const toggleMenuHandler = () => {
    setMenuIsOpen((prev) => !prev);
  };
  return (
    <header className="navbar">
      <div className="navbar-brand">
        <h1 className="navbar-title">Events Booking</h1>
      </div>
      <button className="navbar-toggle" onClick={toggleMenuHandler}>
        {menuIsOpen ? "✖" : "☰"}
      </button>
      <nav className={`navbar-menu ${menuIsOpen ? "active" : ""}`}>
        <ul>
          {!token && (
            <li>
              <NavLink to="/auth" style={{ color: "#ecf0f1", marginRight: '15px', paddingTop: '10px' }}>Login</NavLink>
            </li>
          )}
          <li>
            <NavLink to="/events" style={{ color: "#ecf0f1", marginRight: '15px', paddingTop: '10px' }}>Events</NavLink>
          </li>
          {token && (
            <>
              <li>
                <NavLink to="/bookings" style={{ color: "#ecf0f1", marginRight: '15px', paddingTop: '10 px' }}>Bookings</NavLink>
              </li>
              <li>
                <button className="navbar-logout" onClick={logout}>Logout</button>
              </li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default MainNavigation;
