import React, { useState, useEffect } from "react";
import Backdrop from "../components/Backdrop/Backdrop";
import { useAuth } from "../context/auth-context";
import Spinner from "../components/Spinner/Spinner";
import EventList from "../components/Events/EventList";
import CreateEventForm from "../components/Events/CreateEventForm";
import useEvents from "../hooks/useEvents";

import "./Events.css";

const EventsPage = () => {
  const [creating, setCreating] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isBooked, setIsBooked] = useState(false);
  const { auth } = useAuth();
  const { events, isLoading, fetchEvents, createEvent, bookEvent } = useEvents();

  useEffect(() => {
    fetchEvents();
  }, [fetchEvents]);

  const startCreateEventHandler = () => setCreating(true);
  const modalCancelHandler = () => {
    setCreating(false);
    setSelectedEvent(null);
  };

  const modalConfirmHandler = (eventData) => {
    createEvent(eventData);
    setCreating(false);
  };

  const showDetailHandler = (eventId) => {
    const selectedEvent = events.find((event) => event._id === eventId);
    setSelectedEvent(selectedEvent);
    setIsBooked(selectedEvent.isBooked || false);
  };

  const bookEventHandler = () => {
    if (auth.token && !isBooked) {
      bookEvent(selectedEvent._id);
      setIsBooked(true);
    }
  };

  return (
    <>
      {creating && (
        <>
          <Backdrop />
          <Modal
            title="Add Event"
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={modalConfirmHandler}
            confirmText="Confirm"
          >
            <CreateEventForm />
          </Modal>
        </>
      )}
      {selectedEvent && (
        <>
          <Backdrop />
          <Modal
            title={selectedEvent.title}
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={bookEventHandler}
            confirmText={isBooked ? "Already Booked" : "Book it!"}
          >
            <h1>{selectedEvent.title}</h1>
            <h2>{new Date(selectedEvent.date).toLocaleDateString()} - ${selectedEvent.price}</h2>
            <p>{selectedEvent.description}</p>
          </Modal>
        </>
      )}
      {auth.token && (
        <div className="events-control">
          <p>Create a new event:</p>
          <button className="btn" onClick={startCreateEventHandler}>
            Create Event
          </button>
        </div>
      )}
      {isLoading ? <Spinner /> : <EventList events={events} onViewDetail={showDetailHandler} />}
    </>
  );
};

export default EventsPage;
