import React, { useEffect, useState } from "react";
import Spinner from "../Event/Spinner/Spinner";
import BookingList from "./BookingList/BookingList";
import BookingsChart from "./BookingsChart/BookingsChart";
import BookingsControl from "./BookingsControl/BookingsControl";
import { useAuth } from "../../context/auth-context";

const BookingsPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [bookings, setBookings] = useState([]);
  const [outputType, setOutputType] = useState("list");

  const authContext = useAuth();

  const fetchBookings = () => {
    setIsLoading(true);
    const requestBody = {
      query: `query {
        bookings {
          _id
          createdAt
          event {
            _id
            title
            date
            price
          }
        }
      }`
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + authContext.token
      }
    })
      .then(res => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then(resData => {
        const bookings = resData.data.bookings;
        setBookings(bookings);
        setIsLoading(false);
      })
      .catch(err => {
        console.log(err);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    fetchBookings();
  }, [fetchBookings]); // Add fetchBookings to the dependency array

  const deleteBookingHandler = (bookingId) => {
    setIsLoading(true);
    const requestBody = {
      query: `mutation {
        cancelBooking(bookingId: "${bookingId}") {
          _id
          title
        }
      }`
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + authContext.token
      }
    })
      .then(res => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then(_ => {
        setBookings(prevBookings => prevBookings.filter(booking => booking._id !== bookingId));
        setIsLoading(false);
      })
      .catch(err => {
        console.log(err);
        setIsLoading(false);
      });
  };

  const changeOutputTypeHandler = (outputType) => {
    setOutputType(outputType);
  };

  let content = <Spinner />;
  if (!isLoading) {
    content = (
      <>
        <BookingsControl
          activeOutputType={outputType}
          onChange={changeOutputTypeHandler}
        />
        <div>
          {outputType === "list" ? (
            <BookingList
              bookings={bookings}
              onDelete={deleteBookingHandler}
            />
          ) : (
            <BookingsChart bookings={bookings} />
          )}
        </div>
      </>
    );
  }

  return <>{content}</>;
};

export default BookingsPage;
