import React, { useState, useEffect, useContext } from "react";
import AuthContext from "../../context/auth-context";
import Spinner from "../Event/Spinner/Spinner";
import BookingList from "./BookingList/BookingList";
import BookingsChart from "./BookingsChart/BookingsChart";
import BookingsControl from "./BookingsControl/BookingsControl";
import { fetchBookings, deleteBooking } from "../../controllers/bookingsController";

const BookingsPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [bookings, setBookings] = useState([]);
  const [outputType, setOutputType] = useState("list");
  const [error, setError] = useState(null);

  const context = useContext(AuthContext);

  useEffect(() => {
    handleFetchBookings();
  }, []);

  const handleFetchBookings = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const bookingsData = await fetchBookings(context.token);
      setBookings(bookingsData);
    } catch (err) {
      console.error(err);
      setError("Failed to fetch bookings. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteBooking = async (bookingId) => {
    setIsLoading(true);
    setError(null);
    try {
      await deleteBooking(context.token, bookingId);
      setBookings((prevBookings) => prevBookings.filter((booking) => booking._id !== bookingId));
    } catch (err) {
      console.error(err);
      setError("Failed to cancel booking. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const changeOutputTypeHandler = (type) => {
    setOutputType(type);
  };

  let content = <Spinner />;
  if (!isLoading) {
    content = (
      <React.Fragment>
        <BookingsControl activeOutputType={outputType} onChange={changeOutputTypeHandler} />
        {error && <p className="error-message">{error}</p>}
        <div>
          {outputType === "list" ? (
            <BookingList bookings={bookings} onDelete={handleDeleteBooking} />
          ) : (
            <BookingsChart bookings={bookings} />
          )}
        </div>
      </React.Fragment>
    );
  }

  return <React.Fragment>{content}</React.Fragment>;
};

export default BookingsPage;
