import React from "react";
import { Bar } from "react-chartjs-2";
import "./BookingChart.css";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const BOOKINGS_BUCKETS = {
  Cheap: {
    min: 0,
    max: 50,
  },
  Moderate: {
    min: 50,
    max: 100,
  },
  Expensive: {
    min: 100,
    max: 10000000,
  },
};

const BookingsChart = (props) => {
  const chartData = {
    labels: [],
    datasets: [
      {
        label: "Number of Bookings",
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        hoverBackgroundColor: 'rgba(75, 192, 192, 0.8)',
        hoverBorderColor: 'rgba(75, 192, 192, 1)',
        data: [],
      },
    ],
  };

  let values = [];
  for (const bucket in BOOKINGS_BUCKETS) {
    const filteredBookingsCount = props.bookings.reduce((prev, current) => {
      if (
        current.event.price > BOOKINGS_BUCKETS[bucket].min &&
        current.event.price <= BOOKINGS_BUCKETS[bucket].max
      ) {
        return prev + 1;
      } else {
        return prev;
      }
    }, 0);

    values.push(filteredBookingsCount);
    chartData.labels.push(bucket);
  }

  chartData.datasets[0].data = values;

  return (
    <div className="bookings-chart-container">
      <h2 className="bookings-chart-title">Booking Distribution</h2>
      <div className="chart-wrapper">
        <Bar 
          data={chartData} 
          options={{
            maintainAspectRatio: false, // Allows the chart to resize within the defined height and width
            responsive: true, // Allows the chart to resize
            plugins: {
              legend: {
                display: true,
                labels: {
                  color: '#333',
                  font: {
                    size: 14, // Adjust legend font size
                  },
                },
              },
              tooltip: {
                backgroundColor: '#333', // Dark background for tooltip
                titleColor: '#fff',
                bodyColor: '#fff',
                borderColor: '#fff', // White border
                borderWidth: 1,
              },
            },
            scales: {
              y: {
                beginAtZero: true,
                ticks: {
                  color: '#333',
                  font: {
                    size: 12, // Adjust Y-axis font size
                  },
                },
              },
              x: {
                ticks: {
                  color: '#333',
                  font: {
                    size: 12, // Adjust X-axis font size
                  },
                },
              },
            },
          }} 
        />
      </div>
    </div>
  );
};

export default BookingsChart;
