import React from "react";
import { Bar } from "react-chartjs-2";
import "./BookingChart.css";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register the components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const BOOKINGS_BUCKETS = {
  Cheap: {
    min: 0,
    max: 50,
  },
  Moderate: {
    min: 50,
    max: 100,
  },
  Expensive: {
    min: 100,
    max: 10000000,
  },
};

const BookingsChart = (props) => {
  const chartData = {
    labels: [],
    datasets: [
      {
        label: "Number of Bookings",
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        hoverBackgroundColor: 'rgba(75, 192, 192, 0.8)',
        hoverBorderColor: 'rgba(75, 192, 192, 1)',
        data: [],
      },
    ],
  };

  let values = [];
  for (const bucket in BOOKINGS_BUCKETS) {
    const filteredBookingsCount = props.bookings.reduce((prev, current) => {
      if (
        current.event.price > BOOKINGS_BUCKETS[bucket].min &&
        current.event.price <= BOOKINGS_BUCKETS[bucket].max
      ) {
        return prev + 1;
      } else {
        return prev;
      }
    }, 0);

    values.push(filteredBookingsCount);
    chartData.labels.push(bucket);
  }

  chartData.datasets[0].data = values;

  return (
    <div style={{ textAlign: 'center', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)', backgroundColor: '#fff' }}>
      <h2 style={{ marginBottom: '20px', color: '#333' }}>Booking Distribution</h2>
      <Bar 
        data={chartData} 
        options={{
          maintainAspectRatio: false, // Ensures the chart is responsive
          responsive: true, // Allows the chart to resize
          plugins: {
            legend: {
              display: true,
              labels: {
                color: '#333',
                font: {
                  size: 16, // Adjust legend font size
                },
              },
            },
            tooltip: {
              backgroundColor: '#333', // Dark background for tooltip
              titleColor: '#fff',
              bodyColor: '#fff',
              borderColor: '#fff', // White border
              borderWidth: 1,
            },
          },
          scales: {
            y: {
              beginAtZero: true, // Start Y-axis at zero
              ticks: {
                color: '#333',
                font: {
                  size: 14, // Adjust Y-axis font size
                },
              },
            },
            x: {
              ticks: {
                color: '#333',
                font: {
                  size: 14, // Adjust X-axis font size
                },
              },
            },
          },
        }} 
      />
    </div>
  );
};

export default BookingsChart;
