// src/components/Events/EventsPage.js
import React, { useState, useEffect, useRef, useCallback } from "react";
import "./Events.css";
import EventModal from "./EventModal";
import Backdrop from "./Backdrop";
import Spinner from "./Spinner";
import EventList from "./EventList";
import { useAuth } from "../../context/auth-context";

const EventsPage = () => {
  const [creating, setCreating] = useState(false);
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isBooked, setIsBooked] = useState(false);

  const auth = useAuth();
  const titleElRef = useRef();
  const priceElRef = useRef();
  const dateElRef = useRef();
  const descriptionElRef = useRef();

  useEffect(() => {
    fetchEvents();
  }, []);

  const startCreateEventHandler = () => setCreating(true);

  const modalCancelHandler = () => {
    setCreating(false);
    setSelectedEvent(null);
  };

  const fetchEvents = useCallback(() => {
    setIsLoading(true);
    const requestBody = {
      query: `query {
        events {
          _id
          title
          description
          date
          price
          creator {
            _id
            email
          }
        }
      }`,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed to fetch events");
        }
        return res.json();
      })
      .then((resData) => {
        setEvents(resData.data.events);
        setIsLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setIsLoading(false);
      });
  }, []);

  const showDetailHandler = (eventId) => {
    const selectedEvent = events.find((e) => e._id === eventId);
    setSelectedEvent(selectedEvent);
    // Fetch booking status
    fetchBookingStatus(selectedEvent._id);
  };

  const fetchBookingStatus = (eventId) => {
    fetch(`http://localhost:8000/graphql`, {
      method: "POST",
      body: JSON.stringify({
        query: `
          query {
            userBookings {
              event {
                _id
              }
            }
          }
        `,
      }),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + auth.token,
      },
    })
      .then((res) => res.json())
      .then((resData) => {
        const userBookings = resData.data.userBookings;
        const isEventBooked = userBookings.some((booking) => booking.event._id === eventId);
        setIsBooked(isEventBooked);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  const bookEventHandler = () => {
    if (!auth.token || isBooked) {
      alert("Booking already done for this event.");
      return;
    }

    const requestBody = {
      query: `mutation {
        bookEvent(eventId: "${selectedEvent._id}") {
          _id
          createdAt
          updatedAt
        }
      }`,
    };

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + auth.token,
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed to book event");
        }
        return res.json();
      })
      .then(() => {
        setSelectedEvent(null);
        setIsBooked(true);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <>
      {creating && (
        <EventModal
          title="Add Event"
          canCancel
          onCancel={modalCancelHandler}
          onConfirm={modalConfirmHandler}
          confirmText="Confirm"
          titleElRef={titleElRef}
          priceElRef={priceElRef}
          dateElRef={dateElRef}
          descriptionElRef={descriptionElRef}
        />
      )}
      {selectedEvent && (
        <>
          <Backdrop />
          <EventModal
            title={selectedEvent.title}
            canCancel
            canConfirm
            onCancel={modalCancelHandler}
            onConfirm={bookEventHandler}
            confirmText={auth.token ? (isBooked ? "Already Booked" : "Book it!") : "Confirm"}
            disableConfirmButton={isBooked}
            eventDetails={selectedEvent}
          />
        </>
      )}
      {auth.token && (
        <div className="events-control">
          <p>Share your own events!</p>
          <button className="btn" onClick={startCreateEventHandler}>
            Create Event
          </button>
        </div>
      )}
      {isLoading ? (
        <Spinner />
      ) : (
        <EventList events={events} authUserId={auth.userId} onViewDetails={showDetailHandler} />
      )}
    </>
  );
};

export default EventsPage;
