import React, { useState, useContext, useRef } from "react";
import AuthContext from "../../context/auth-context";
import "./Auth.css"; // Ensure you have the CSS file imported

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const authContext = useContext(AuthContext);

  const emailRef = useRef();
  const passwordRef = useRef();
  const confirmPasswordRef = useRef();

  const handleSubmit = (event) => {
    event.preventDefault();

    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    // Validations
    if (email.trim().length === 0) {
      setErrorMessage("Email is required.");
      return;
    }
    if (password.trim().length === 0) {
      setErrorMessage("Password is required.");
      return;
    }
    if (!isLogin && password.length < 6) {
      setErrorMessage("Password must be at least 6 characters long.");
      return;
    }
    if (!isLogin && password !== confirmPasswordRef.current.value) {
      setErrorMessage("Passwords do not match.");
      return;
    }

    let requestBody = {
      query: `
        query {
          login(email: "${email}", password: "${password}") {
                userId
                token
                tokenExpiration
          }
        }
      `,
    };

    if (!isLogin) {
      requestBody = {
        query: `mutation {
          createUser(userInput: {email: "${email}", password: "${password}"}) {
            _id
            email
          }
        }`,
      };
    }

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then((resData) => {
        console.log(resData); // Log the response to see its structure
        // Check if we're in the login state and the response contains the necessary data
        if (!isLogin) {
          // Handle signup success case if needed
          console.log('User created successfully:', resData.data.createUser);
        } else if (resData.data && resData.data.login) {
          // Proceed if the login data is available
          authContext.login(
            resData.data.login.token,
            resData.data.login.userId,
            resData.data.login.tokenExpiration
          );
        } else {
          // Handle the case where login was unsuccessful
          setErrorMessage("Login failed. Please check your credentials.");
        }
      })
      .catch((err) => {
        console.log(err);
        setErrorMessage("An error occurred. Please try again.");
      });
  };

  const handleSwitch = () => {
    setIsLogin((prevIsLogin) => !prevIsLogin);
    setErrorMessage(""); // Clear error message on switch
  };

  return (
    <form className="auth-form" onSubmit={handleSubmit}>
      <div className="form-control">
        <label htmlFor="email">E-mail</label>
        <input type="email" id="email" ref={emailRef} required />
      </div>

      <div className="form-control">
        <label htmlFor="password">Password</label>
        <input type="password" id="password" ref={passwordRef} required />
      </div>

      {!isLogin && ( // Show confirm password field only for signup
        <div className="form-control">
          <label htmlFor="confirm-password">Confirm Password</label>
          <input
            type="password"
            id="confirm-password"
            ref={confirmPasswordRef}
            required
          />
        </div>
      )}

      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <div className="form-actions">
        <button type="submit">Submit</button>
        <button type="button" onClick={handleSwitch}>
          Switch to {isLogin ? "Signup" : "Login"}
        </button>
      </div>
    </form>
  );
};

export default AuthPage;
