import React from "react";
import "./Modal.css";

const Modal = (props) => {
  console.log(props);

  const renderChildren = () => {
    return React.Children.map(props.children, (child, index) => {
      // Conditional rendering based on the index
      if (index === 0) {
        return (
          <div key={index} className="modal-child">
            <h2 style={{ display: "inline" }}>Event Title: {child}</h2>
          </div>
        );
      } else if (index === 1) {
        // Extract the date from the child string
        const [date] = child.split(" - "); // Assumes the format is "date - price"
        return (
          <div key={index} className="modal-child">
            <p style={{ display: "inline" }}>Date: {date}</p>
            <p style={{ display: "inline" }}>Price: {child.split(" - ")[1]}</p>
          </div>
        );
      } else if (index === 2) {
        return (
          <div key={index} className="modal-child">
            <p style={{ display: "inline" }}>Description: {child}</p>
          </div>
        );
      }
      return null; // For any other indices, return null
    });
  };

  return (
    <div className="modal-overlay" onClick={props.onCancel}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <header className="modal-header">
          <h1>Title - {props.title}</h1>
        </header>
        <section className="modal-content">
          {renderChildren()}
        </section>
        <section className="modal-actions">
          {props.canCancel && (
            <button className="btn btn-cancel" onClick={props.onCancel}>
              Cancel
            </button>
          )}
          {props.canConfirm && (
            <button className="btn btn-confirm" onClick={props.onConfirm}>
              {props.confirmText}
            </button>
          )}
        </section>
      </div>
    </div>
  );
};

export default Modal;
