import React, { useState, useContext, useRef } from "react";
import { useNavigate } from "react-router-dom"; // Importing useNavigate
import "./Auth.css";
import AuthContext from "../context/auth-context";

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const [isRedirect, setIsRedirect] = useState(false); 
  const authContext = useContext(AuthContext);
  const navigate = useNavigate();

  const emailRef = useRef();
  const passwordRef = useRef();
  const confirmPasswordRef = useRef();

  const handleSubmit = (event) => {
    event.preventDefault();

    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    // Trim and validate email and password
    if (email.trim().length === 0 || password.trim().length === 0) {
      setErrorMessage("Email and password are required.");
      return;
    }

    // Email validation regex
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailPattern.test(email)) {
      setErrorMessage("Please enter a valid email.");
      return;
    }

    // Password validation
    if (!isLogin) {
      if (password.length < 6) {
        setErrorMessage("Password must be at least 6 characters long.");
        return;
      }
      if (password !== confirmPasswordRef.current.value) {
        setErrorMessage("Passwords do not match.");
        return;
      }
    }

    let requestBody;

    // Define the request based on login/signup state
    if (isLogin) {
      requestBody = {
        query: `
          query {
            login(email: "${email}", password: "${password}") {
              userId
              token
              tokenExpiration
            }
          }
        `,
      };
    } else {
      requestBody = {
        query: `mutation {
          createUser(userInput: {email: "${email}", password: "${password}"}) {
            _id
            email
          }
        }`,
      };
    }

    fetch("http://localhost:8000/graphql", {
      method: "POST",
      body: JSON.stringify(requestBody),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error("Failed");
        }
        return res.json();
      })
      .then((resData) => {
        // Check if login is successful
        if (isLogin && resData.data.login.token) {
          authContext.login(
            resData.data.login.token,
            resData.data.login.userId,
            resData.data.login.tokenExpiration
          );
          setIsRedirect(true); // Set redirect state to true
          navigate("/events"); // Navigate to events page after login
        } else if (!isLogin) {
          console.log("User created successfully:", resData.data.createUser);
        }
      })
      .catch((err) => {
        console.log(err);
        setErrorMessage("An error occurred. Please try again.");
      });
  };

  const handleSwitch = () => {
    setIsLogin((prev) => !prev);
    setErrorMessage(""); // Clear error message on switch
  };

  // Redirect to events page if isRedirect is true
  if (isRedirect) {
    return <Redirect to="/events" replace />;
  }

  return (
    <form className="auth-form" onSubmit={handleSubmit}>
      <div className="form-control">
        <label htmlFor="email">E-mail</label>
        <input type="email" id="email" ref={emailRef} />
      </div>

      <div className="form-control">
        <label htmlFor="password">Password</label>
        <input type="password" id="password" ref={passwordRef} />
      </div>

      {!isLogin && (
        <div className="form-control">
          <label htmlFor="confirm-password">Confirm Password</label>
          <input
            type="password"
            id="confirm-password"
            ref={confirmPasswordRef}
          />
        </div>
      )}

      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <div className="form-actions">
        <button type="submit">Submit</button>
        <button type="button" onClick={handleSwitch}>
          Switch to {isLogin ? "Signup" : "Login"}
        </button>
      </div>
    </form>
  );
};

export default AuthPage;
